import React, { FC } from 'react';
import { RouteComponentProps } from 'react-router-dom';
import { productItems } from '../../../data/data';
import './style.scss';

type TParams = { id: string | undefined };

const ProductInfo: FC<RouteComponentProps<TParams>> = ({ match }) => {
  const product = productItems.find((item) => item.id === match.params.id);
  return (
    <div className="product-info">
      <div className="product-info__gallery">
        <img src={product?.image} alt="" />
      </div>
      <div className="product-info__content">
        <h3 className="product__name">Name</h3>
        <h4 className="product__description">
          Lorem, ipsum dolor sit amet consectetur adipisicing elit. Asperiores
          repellat nam rem dolore mollitia facere ipsa minus, deserunt in saepe
          accusantium exercitationem quibusdam pariatur iusto reiciendis aliquid
          praesentium enim dignissimos!
        </h4>
        <div className="product__reviews">
          <p className="product__reviews-text">6 reviews</p>
          <div className="product__reviews-stars"></div>
        </div>
        <div className="product__price">Price</div>
        <div className="product__colors">
          <div className="product__color"></div>
        </div>
        <div className="product__sizes">
          <div className="product__size"></div>
        </div>
        <div className="product__button-bag">Add to bag</div>
        <div className="product__button-wishlist">Add to wishlist</div>
      </div>
    </div>
  );
};

export default ProductInfo;
